// SPDX-License-Identifier: MIT

pragma solidity ^0.8.17;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "./Errors.sol";
import "hardhat/console.sol";

contract GuardiansManager is Ownable, ReentrancyGuard {
    // * STATE VARIABLES
    uint256 private addGuardianDelay;
    uint256 private lastGuardianAddTime;
    uint256 private changeGuardianDelay;
    uint256 private lastGuardianChangeTime;
    uint256 private removeGuardianDelay;
    uint256 private lastGuardianRemovalTime;

    uint256 internal requiredConfirmations;

    address[] internal guardians;

    // * FUNCTIONS
    constructor() {
        lastGuardianAddTime = block.timestamp;
        addGuardianDelay = 1 days;
        lastGuardianChangeTime = block.timestamp;
        changeGuardianDelay = 1 days;
        lastGuardianRemovalTime = block.timestamp;
        removeGuardianDelay = 3 days;
    }

    // * FUNCTIONS - EXTERNAL
    function addGuardian(address guardian) external onlyOwner nonReentrant {
        if (block.timestamp < lastGuardianAddTime + addGuardianDelay) {
            revert Error__CanOnlyAddAfterDelayPeriod("GuardiansManager");
        }

        guardians.push(guardian);
        lastGuardianAddTime = block.timestamp;
        updateRequiredConfirmations();
    }

    function changeGuardian(
        address from,
        address to
    ) external onlyOwner nonReentrant {
        if (block.timestamp < lastGuardianChangeTime + changeGuardianDelay) {
            revert Error__CanOnlyChangeAfterDelayPeriod("GuardiansManager");
        }

        address[] memory guardiansCopy = guardians;

        // * using this variable so we should not update the state variable if address does not exits.
        bool exist = false;

        for (uint256 i = 0; i < guardiansCopy.length; i++) {
            if (guardiansCopy[i] == from) {
                exist = true;
                guardiansCopy[i] = to;
                break;
            }
        }

        if (exist) {
            guardians = guardiansCopy;
            lastGuardianChangeTime = block.timestamp;
        } else {
            revert Error__GuardianDoesNotExist("GuardiansManager");
        }
    }

    function removeGuardian(address guardian) external onlyOwner nonReentrant {
        if (block.timestamp < lastGuardianRemovalTime + removeGuardianDelay) {
            revert Error__CanOnlyRemoveAfterDelayPeriod("GuardiansManager");
        }

        if (guardians.length <= 0) {
            revert Error__GuardiansListIsEmpty("GuardiansManager");
        }

        if (!doesGuardianExist(guardian)) {
            revert Error__GuardianDoesNotExist("GuardiansManager");
        }

        address[] memory guardiansCopy = guardians;

        // * for local arrays we need to declare the size at the initialization time.
        // * we are removing the 1 guardian so the updated array will be less than 1 the length of existing array.
        address[] memory updatedCopy = new address[](guardiansCopy.length - 1);
        uint256 index = 0;

        // * using this variable so we should not update the state variable if address does not exits.
        bool exist = false;

        for (uint256 i = 0; i < guardiansCopy.length; i++) {
            if (guardiansCopy[i] == guardian) {
                exist = true;
                continue;
            } else {
                updatedCopy[index] = guardiansCopy[i];
                index++;
            }
        }

        if (exist) {
            guardians = updatedCopy;
            lastGuardianRemovalTime = block.timestamp;
        }
    }

    // * FUNCTION - PRIVATE

    function updateRequiredConfirmations() private {
        requiredConfirmations = (guardians.length / 2) + 1;
    }

    // * FUNCTIONS - VIEW & PURE - EXTERNAL

    function getLastGuardianAddTime() external view returns (uint256) {
        return lastGuardianAddTime;
    }

    function getAddGuardianDelay() external view returns (uint256) {
        return addGuardianDelay;
    }

    function getLastGuardianChangeTime() external view returns (uint256) {
        return lastGuardianChangeTime;
    }

    function getChangeGuardianDelay() external view returns (uint256) {
        return changeGuardianDelay;
    }

    function getLastGuardianRemovalTime() external view returns (uint256) {
        return lastGuardianRemovalTime;
    }

    function getRemoveGuardianDelay() external view returns (uint256) {
        return removeGuardianDelay;
    }

    function getGuardians() external view returns (address[] memory) {
        return guardians;
    }

    // * FUNCTIONS - VIEW & PURE - INTERNAL

    function doesGuardianExist(address caller) internal view returns (bool) {
        address[] memory guardiansCopy = guardians;

        for (uint256 i = 0; i < guardiansCopy.length; i++) {
            if (caller == guardiansCopy[i]) {
                return true;
            }
        }

        return false;
    }
}

contracts/OwnershipManager.sol.content:
// SPDX-License-Identifier: MIT

import "./GuardiansManager.sol";
import "./Errors.sol";

pragma solidity ^0.8.17;

contract OwnershipManager is GuardiansManager {
    // * STATE VARIABLES
    uint256 private lastOwnerUpdateRequestTime;
    uint256 private ownerUpdateConfirmationTime;
    uint256 private noOfConfirmations;

    bool private isOwnerUpdateRequested;

    address private tempAddress;

    mapping(address => bool) private isOwnershipConfimedByGuardian;

    // * FUNCTIONS
    constructor() {
        ownerUpdateConfirmationTime = 2 hours;
    }

    function requestToUpdateOwner(address newOwnerAddress) external {
        if (newOwnerAddress == owner()) {
            revert Error__AddressAlreadyAnOwner(
                "OwnershipManager",
                newOwnerAddress
            );
        }

        if (guardians.length <= 0) {
            revert Error__GuardiansListIsEmpty("OwnershipManager");
        }

        if (!doesGuardianExist(msg.sender)) {
            revert Error__AddressNotFoundAsGuardian(
                "OwnershipManager",
                msg.sender
            );
        }

        lastOwnerUpdateRequestTime = block.timestamp;
        tempAddress = newOwnerAddress;
        isOwnershipConfimedByGuardian[msg.sender] = true;
        noOfConfirmations++;
        isOwnerUpdateRequested = true;
    }

    function confirmUpdateOwnerRequest() external {
        if (guardians.length <= 0) {
            revert Error__GuardiansListIsEmpty("OwnershipManager");
        }

        if (!isOwnerUpdateRequested) {
            revert Error__UpdateNotRequested("OwnershipManager");
        }

        if (isOwnershipConfimedByGuardian[msg.sender]) {
            revert Error__AlreadyConfirmedByGuardian(
                "OwnershipManager",
                msg.sender
            );
        }

        if (
            block.timestamp >
            lastOwnerUpdateRequestTime + ownerUpdateConfirmationTime
        ) {
            resetOwnershipVariables();
            revert Error__RequestTimeExpired("OwnershipManager");
        }

        if (!doesGuardianExist(msg.sender)) {
            revert Error__AddressNotFoundAsGuardian(
                "OwnershipManager",
                msg.sender
            );
        }

        isOwnershipConfimedByGuardian[msg.sender] = true;
        noOfConfirmations++;

        if (noOfConfirmations >= requiredConfirmations) {
            // * Ownable internal function without access restriction.
            _transferOwnership(tempAddress);
            resetOwnershipVariables();
        }
    }

    // * FUNCTION - PRIVATE
    function resetOwnershipVariables() private {
        isOwnerUpdateRequested = false;
        noOfConfirmations = 0;

        address[] memory guardiansCopy = guardians;

        for (uint256 i = 0; i < guardiansCopy.length; i++) {
            if (isOwnershipConfimedByGuardian[guardiansCopy[i]]) {
                isOwnershipConfimedByGuardian[guardiansCopy[i]] = false;
            }
        }
    }

    // * FUNCTION - VIEW & PURE
    function getIsOwnerUpdateRequested() external view returns (bool) {
        return isOwnerUpdateRequested;
    }

    function getIsOwnershipConfimedByGuardian(
        address guardian
    ) external view returns (bool) {
        return isOwnershipConfimedByGuardian[guardian];
    }

    function getLastOwnerUpdateRequestTime() external view returns (uint256) {
        return lastOwnerUpdateRequestTime;
    }

    function getOwnerUpdateConfirmationTime() external view returns (uint256) {
        return ownerUpdateConfirmationTime;
    }

    function getNoOfConfirmations() external view returns (uint256) {
        return noOfConfirmations;
    }
}

